
import React from 'react';
import { World } from '../App';

interface PixelSakuyaProps {
  world: World;
}

const SAKUYA_SPRITESHEET_DATA_URL = 'data:image/png;base64,';

export const PixelSakuya: React.FC<PixelSakuyaProps> = ({ world }) => {
  const spriteStyle: React.CSSProperties = {
    backgroundImage: `url(${SAKUYA_SPRITESHEET_DATA_URL})`,
    backgroundSize: '800% 800%', // Spritesheet is 8x8 frames
    backgroundPosition: '0% 0%', // Start at the first row (down-facing)
    imageRendering: 'pixelated',
  };

  return (
    <div
      className="w-full h-full sakuya-idle-anim"
      style={spriteStyle}
      aria-label="Animated pixel art of Izayoi Sakuya"
    ></div>
  );
};
